INSERT INTO `ecs_article` ( `article_id`, `cat_id`, `title`, `content`, `author`, `author_email`, `keywords`, `article_type`, `is_open`, `add_time`, `file_url`, `open_type`, `link`, `description`, `click_count`, `rel_clickcount`, `love` ) VALUES  ('131', '20', '谁能给你真正的幸福 谁会陪你走到最后', '<p style=\"text-indent:0px;color:#000000;\">\r\n	<span style=\"color:#003399;font-size:16px;\"><strong><strong><span><span style=\"line-height:25px;\"><span style=\"font-size:10pt;\"><strong><span><img alt=\"\" src=\"https://mmbiz.qlogo.cn/mmbiz/NPv8Ib9HvuIYJ7ygEhEAhRpcRQlxsEMlo9k5M7JWepianJicv22GaBDyBUHNwWoV1IPWibfiboZvAXCl9qC2bkIRiaA/0\" width=\"300\" height=\"36\" /><br />\r\n</span></strong></span></span></span></strong>\r\n</p>\r\n<p>\r\n	 提醒:点击标题下方的“</span><span><span style=\"color:#0070c0;\">实实商城</span></span><span style=\"font-size:10pt;\">”即可免费订阅我，随时发现，采集，分享你喜欢的一切。</span></span></span></strong> \r\n</p>\r\n</strong></span> \r\n<p style=\"text-indent:0px;color:#000000;\">\r\n	<span style=\"color:#003399;font-size:16px;\"><strong></strong></span>&nbsp;\r\n</p>\r\n<p style=\"text-indent:0px;color:#000000;\">\r\n	<span style=\"color:#003399;font-size:16px;\"><strong>一生只要一次婚姻，并且对方会一直爱你一直疼你到最后，这是每个人都想要的。你想知道谁会陪你走到最后吗？什么样的人会是陪你走到最后的人呢？下面心理测试将会为你揭晓。</strong></span> \r\n</p>\r\n<p style=\"text-indent:0px;color:#000000;\" align=\"center\">\r\n	<a href=\"http://www.wed114.cn/jiehun/aiqingceshi/20130925111201.html\"><img alt=\"\" src=\"http://www.wed114.cn/jiehun/uploads/allimg/130926/1I253F40-0.jpg\" width=\"288\" height=\"193\" /></a> \r\n</p>\r\n<p style=\"text-indent:0px;color:#000000;\">\r\n	<span style=\"color:#cc33e5;font-size:16px;\"><strong>每天看电视报纸的时候，你最感兴趣的是哪一类八卦话题?</strong></span> \r\n</p>\r\n<p style=\"text-indent:0px;color:#000000;\">\r\n	<span style=\"color:#003399;font-size:16px;\">1、艺人的情变消息</span> \r\n</p>\r\n<p style=\"text-indent:0px;color:#000000;\">\r\n	<span style=\"color:#003399;font-size:16px;\">2、贪污受贿的爆料</span> \r\n</p>\r\n<p style=\"text-indent:0px;color:#000000;\">\r\n	<span style=\"color:#003399;font-size:16px;\">3、明星的性别倾向</span> \r\n</p>\r\n<p style=\"text-indent:0px;color:#000000;\">\r\n	<span style=\"color:#003399;font-size:16px;\">4、大企业的坏内幕</span> \r\n</p>\r\n<p style=\"text-indent:0px;color:#000000;\" align=\"center\">\r\n	<a href=\"http://www.wed114.cn/jiehun/aiqingceshi/20130925111201.html\"><img alt=\"\" src=\"http://www.wed114.cn/jiehun/uploads/allimg/130926/1I2532956-1.jpg\" width=\"288\" height=\"204\" /></a> \r\n</p>\r\n<p style=\"text-indent:0px;color:#000000;\">\r\n	<span style=\"color:#64451d;font-size:16px;\"><strong>测试结果：</strong></span> \r\n</p>\r\n<p style=\"text-indent:0px;color:#000000;\">\r\n	<span style=\"color:#cc33e5;font-size:16px;\"><strong>1、艺人的情变消息</strong></span> \r\n</p>\r\n<p style=\"text-indent:0px;color:#000000;\">\r\n	<span style=\"color:#003399;font-size:16px;\">想要靠近你的人很多是被你开朗的笑容所吸引的。当你在选择另一半的时候，还是会希望不要成为恋爱的主导者，你会向往扮演小女生(小男生)的角色，享受对方对你的照顾和呵护。生活</span><span style=\"color:#003399;font-size:16px;\">爱热闹一点爱点八卦爱家顾家的人比较适合给你真正的幸福哦。</span> \r\n</p>\r\n<p style=\"text-indent:0px;color:#000000;\">\r\n	<span style=\"color:#cc33e5;font-size:16px;\"><strong>2、贪污受贿的爆料</strong></span> \r\n</p>\r\n<p style=\"text-indent:0px;color:#000000;\">\r\n	<span style=\"color:#003399;font-size:16px;\">越是不驯的另一半，越是能挑起你想要去征服的欲望。你总是没办法抗拒坏男人或坏女人对你的吸引力，你只能乖乖追随在他们身后，只有当你也学会使坏，你们才会有平等交手的机会。桀骜不驯的另一半比较能引起你的幸福，让你感觉到幸福哦。</span> \r\n</p>\r\n<p style=\"text-indent:0px;color:#000000;\">\r\n	<span style=\"color:#cc33e5;font-size:16px;\"><strong>3、明星的性别倾向</strong></span> \r\n</p>\r\n<p style=\"text-indent:0px;color:#000000;\">\r\n	<span style=\"color:#003399;font-size:16px;\">你喜欢挑剔，不容易和他人维持很长的关系。如果一时双方都按捺不下怒气，可能就会走上分手这条道路。你唯一的爱情克星就是那种懂得以柔制刚的高手，遇到那样的对象你可就没辄了，乖巧温柔的人可以给你幸福哦。</span> \r\n</p>\r\n<p style=\"text-indent:0px;color:#000000;\">\r\n	<span style=\"color:#cc33e5;font-size:16px;\"><strong>4、大企业的坏内幕</strong></span> \r\n</p>\r\n<p style=\"text-indent:0px;color:#000000;\">\r\n	<span style=\"color:#003399;font-size:16px;\">你有点固执，不会轻易受到别人的影响，也爱对别人说教，希望大家都能被你说服。但是不一定总是能够成为众人的意见领袖，你这好为人师的性格就只好在两人世界彰显其威力，凡是能让你发挥所长，愿意静静聆听你的长篇大论，又不怕孤独，可以如耶稣门徒一般随你的理想而行的人，那就是你的真爱了，那样的人便能给你幸福哦。</span> \r\n</p>', '', '', '', '0', '1', '1427854083', '', '0', 'http://', '', '11022', '0', '407');
INSERT INTO `ecs_article` ( `article_id`, `cat_id`, `title`, `content`, `author`, `author_email`, `keywords`, `article_type`, `is_open`, `add_time`, `file_url`, `open_type`, `link`, `description`, `click_count`, `rel_clickcount`, `love` ) VALUES  ('55', '16', '9大品质，让老板为你点赞', '<p style=\"text-indent:0pt;background:#ffffff;\" class=\"p\">\r\n	<span style=\"font-style:normal;font-family:宋体;background:#ffffff;color:#3e3e3e;font-size:12pt;font-weight:normal;\"><strong><span><span style=\"line-height:25px;\"><span style=\"font-size:10pt;\"><strong><span> </span></strong></span></span></span></strong></span> \r\n</p>\r\n<p style=\"text-indent:0px;color:#000000;\">\r\n	<strong><strong><span style=\"font-size:16px;\"><strong><span><span style=\"line-height:25px;\"><span style=\"font-size:10pt;\"><strong><span><span style=\"color:#003399;font-size:16px;\"><strong><strong><span><span style=\"line-height:25px;\"><span style=\"font-size:10pt;\"><strong><span><img alt=\"\" src=\"https://mmbiz.qlogo.cn/mmbiz/NPv8Ib9HvuIYJ7ygEhEAhRpcRQlxsEMlo9k5M7JWepianJicv22GaBDyBUHNwWoV1IPWibfiboZvAXCl9qC2bkIRiaA/0\" width=\"300\" height=\"35\" /></span></strong></span></span></span></strong></strong></span></span></strong></span></span></span></strong></span></strong></strong> \r\n</p>\r\n<p style=\"text-align:left;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:12pt;font-weight:bold;\"></span> \r\n</p>\r\n<p style=\"text-align:left;\" class=\"MsoNormal\">\r\n	<span style=\"font-family:宋体;font-size:14pt;font-weight:bold;\">1.高目标，高要求</span><span style=\"FONT-FAMILY: ; COLOR: rgb(62,62,62)\" helvetica=\"\" neue?;font-size:9pt;font-style:normal;font-weight:normal;?=\"\"></span> \r\n</p>\r\n<p style=\"text-indent:0pt;background:#ffffff;\" class=\"p\">\r\n	<span style=\"font-style:normal;font-family:宋体;background:#ffffff;color:#3e3e3e;font-size:12pt;font-weight:normal;\">这是一个最重要和最有效的与普通员工区分开的方法。</span><span style=\"font-style:normal;font-family:Arial;background:#ffffff;color:#3e3e